sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ( newValue == '') {
     return;
   }

   var whatneeded = g_form.getValue('u_what_needed');

   // Clear all of the choices from the What needed field choice list

   g_form.clearOptions('u_what_needed');

   // If the value of the Request type field is hr, add
   // two hr choices and other to the What needed field choice list

if(newValue == 'hr'){
   g_form.addOption('u_what_needed', 1, 'hr option 1');
   g_form.addOption('u_what_needed', 2, 'hr option 2');
   g_form.addOption('u_what_needed', 3, 'other');
}

if(newValue == 'facilities'){
   g_form.addOption('u_what_needed', 1, 'fr option 1');
   g_form.addOption('u_what_needed', 2, 'fr option 2');
   g_form.addOption('u_what_needed', 3, 'other fa');
}

if(newValue == 'legal'){
   g_form.addOption('u_what_needed', 1, 'legal option 1');
   g_form.addOption('u_what_needed', 2, 'legal option 2');
   g_form.addOption('u_what_needed', 3, 'other legal');
}

	if(isLoading && !g_form.isNewRecord()){
      g_form.setValue('u_what_needed', whatneeded);
    }


 }
